mapper.select:
select * from product where 1 = 1 

	and name like '%${name}%' 


	and tags like '%${tags}%'  


	and price > #{priceMin}  


	and price < #{priceMax}  


	order by ${orderBy} ${sort}   


	limit #{offset},#{pageSize}

select count(*) from product where 1 = 1 

	and name like '%${name}%' 


	and tags in(${tags})  


	and price > #{priceMin}  


	and price < #{priceMax}
